mapper.select:
SELECT 
FROM hh_voucher V


    AND V.voucher_Id = #{voucherId, jdbcType = VARCHAR}


    AND V.customer_Id = #{customerId, jdbcType = VARCHAR}


    AND V.voucher_Date = #{voucherDate, jdbcType = VARCHAR}

SELECT V.customer_Id AS customerId,C.customer_Name AS customerName , SUM(V.amount) AS amount, COUNT(voucher_Id) AS voucherNnm FROM hh_voucher V LEFT JOIN hh_customer C ON V.customer_Id = C.customer_Id
AND C.customer_Name LIKE "%"#{customerName, jdbcType = VARCHAR}"%"
AND V.voucher_Date >= #{voucherDate_begin, jdbcType = TIMESTAMP}
AND V.voucher_Date <= #{voucherDate_end, jdbcType = TIMESTAMP}
GROUP BY V.customer_Id ORDER BY amount DESC

SELECT  V.voucher_Id AS voucherId, V.customer_Id AS customerId, V.amount AS amount,
V.voucher_Date AS voucherDate,
V.voucher_Maker AS voucherMaker, V.remark AS remark,V.creater AS creater, V.created_time AS createdTime,
V.modifier AS modifier, V.modified_time  AS modifiedTime,C.customer_Name AS customerName
FROM hh_voucher V
LEFT JOIN hh_customer C ON V.customer_Id = C.customer_Id


    AND V.customer_Id = #{customerId, jdbcType = VARCHAR}


    AND C.customer_Name LIKE "%"#{customerName, jdbcType = VARCHAR}"%"


    AND V.voucher_Id = #{voucherId, jdbcType = VARCHAR}


     AND V.voucher_Date >= #{voucherDate_begin, jdbcType = TIMESTAMP} 


    AND V.voucher_Date <= #{voucherDate_end, jdbcType = TIMESTAMP}


ORDER BY  V.voucher_Date DESC

SELECT SUM(V.amount) AS totalAmount
FROM hh_voucher V
WHERE V.customer_Id = #{customerId, jdbcType = VARCHAR}
